import React from "react";
import { Link } from "react-router-dom";

export default function SidebarRenter({ onLogout, onSelect }) {
  const menuStyle = {
    background: "#2C3E50",
    color: "#fff",
    minHeight: "100vh",
    padding: "20px",
    width: "240px",
    display: "flex",
    flexDirection: "column",
  };

  const buttonStyle = {
    padding: "12px 14px",
    margin: "8px 0",
    border: "none",
    borderRadius: "6px",
    background: "#34495E",
    color: "#fff",
    textAlign: "left",
    cursor: "pointer",
    fontSize: "15px",
    textDecoration: "none",
    display: "block",
    width: "100%",
    // --- THIS IS THE FINAL FIX ---
    boxSizing: "border-box",
    // ----------------------------
  };

  const logoutStyle = {
    ...buttonStyle,
    marginTop: "auto",
    background: "#E74C3C",
  };

  return (
    <div style={menuStyle}>
      <h2 style={{ marginBottom: "20px", fontSize: "20px" }}>Renter</h2>
      <button style={buttonStyle} onClick={() => onSelect("rentDetails")}>
        üìÑ Rent Details
      </button>
      <button style={buttonStyle} onClick={() => onSelect("wishlist")}>
        ‚ù§Ô∏è Wishlist
      </button>
      <button style={buttonStyle} onClick={() => onSelect("messages")}>
        üí¨ Messages
      </button>
      <Link to="/home" style={buttonStyle}>
        üè† Advertisements
      </Link>
      <button style={logoutStyle} onClick={onLogout}>
        üö™ Logout
      </button>
    </div>
  );
}
