import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";

axios.defaults.withCredentials = true;
const API = process.env.REACT_APP_API_URL || "http://localhost:1629/api";

export default function Navbar() {
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    axios
      .get(`${API}/auth/profile`)
      .then((res) => setUser(res.data))
      .catch(() => setUser(null));
  }, []);

  const handleDashboardClick = () => {
    if (!user) {
      navigate("/login");
    } else if (user.role.toLowerCase() === "admin") {
      // FIX 1: Corrected path from "/admin/dashboard" to "/dashboard/admin" to match your App.js
      navigate("/dashboard/admin");
    } else if (user.role === "Landlord") {
      navigate("/dashboard/landlord");
    } else if (user.role === "Renter") {
      navigate("/dashboard/renter");
    }
  };

  const handleLogout = async () => {
    try {
      await axios.post(`${API}/auth/logout`);
      setUser(null);
      navigate("/login");
    } catch {
      alert("Logout failed");
    }
  };

  // --- Styles ---
  const navStyle = {
    display: "flex",
    gap: "15px",
    padding: "20px 40px", // Increased horizontal padding
    alignItems: "center",
    background: "rgba(255,255,255,0.6)",
    boxShadow: "0 1px 6px rgba(0,0,0,0.05)",
  };
  const linkStyle = {
    textDecoration: "none",
    color: "#333",
    fontWeight: 600,
    fontSize: '1rem',
  };
  const brandStyle = {
    marginRight: 'auto', // Pushes all other items to the right
    fontWeight: 900,
    fontSize: '1.8rem',
    letterSpacing: '0.8px',
    fontFamily: "'Poppins', sans-serif",
    color: '#2c3e50',
  };
  const buttonStyle = {
    ...linkStyle,
    background: "none",
    border: "none",
    cursor: "pointer",
    padding: 0,
  };

  // --- FIX 2: Corrected the entire return statement ---
  return (
    <nav style={navStyle}>
      <Link to="/" style={{ ...linkStyle, ...brandStyle }}>
        Rental Connect
      </Link>
      
      {user ? (
        // If user is logged IN, show Dashboard and Logout
        <>
          <span style={{ fontWeight: 500 }}>Welcome, {user.name}!</span>
          <button style={buttonStyle} onClick={handleDashboardClick}>
            Dashboard
          </button>
          <button style={buttonStyle} onClick={handleLogout}>
            Logout
          </button>
        </>
      ) : (
        // If user is logged OUT, show Login and Register
        <>
          <Link to="/login" style={linkStyle}>Login</Link>
          <Link to="/register" style={linkStyle}>Register</Link>
        </>
      )}
    </nav>
  );
}
